import React, { useState } from "react";
import { Card } from "react-bootstrap";
import { createSensorEvent } from "../sensor/SensorService";


const SensorEventForm = () => {

    const [sensor, setSensor] = useState({
        name: "",
        sensorId: "",
        value: "" 
    })

    const handleInputChange = (e) => {
        setSensor({...sensor, [e.target.name]: e.target.value})
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(sensor)
    }

  return (
    <div className="row">
      <Card border="info" style={{ width: "18rem" }}>
        <Card.Header>Sensor Event</Card.Header>
        <Card.Body>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="text"
                name="name"
                placeholder="name"
                className="form-control"
                onChange={handleInputChange}
                autofocus
              />
            </div>
            <br />

            <div className="form-group">
              <input
                type="text"
                name="sensorId"
                placeholder="Id Sensor"
                className="form-control"
                onChange={handleInputChange}
                autofocus
              />
            </div>
            <br />
            
            <div className="form-group">
              <textarea
                name="value"
                rows={2}
                placeholder="value"
                className="form-control"
                onChange={handleInputChange}
                autofocus
              />
            </div>
            <br />

            <button className="btn btn-primary">
                create event
            </button>
          </form>
        </Card.Body>
      </Card>
    </div>
  );
};

export default SensorEventForm;
