import React, { useState } from "react";
import { useHistory } from "react-router";
import "./SensorItem.css";
import { getSensors } from "./SensorService";

const SensorItem = () => {
  const [sensors, setSensors] = useState([])
  const history = useHistory();
  // const { _id, name, maxval, minval, address, active } = sensor
  // const { coordinates } = sensor.location
  const loadSensor = async () => {
    const resp = await getSensors();
    console.log(resp)
    setSensors(resp.data);
  };

  useEffect(() => {
    loadSensor();
  }, []);
 
  return (
    <div className="col-md-4">
      <div
        className="card card-body sensor-card"
        onClick={() => history.push(`/update/${_id}`)}
      >
        <div className="d-flex justify-content-between">
          <h1>{name}</h1>
          <span className="text-danger">X</span>
        </div>
        <p>{address}</p>
        <p>Coordenadas: {coordinates[0]}</p>
        <p>{active}</p>
        <p>MinVal: {minval}</p>
        <p>MaxVal: {maxval}</p>
      </div>
    </div>
  );
};

export default SensorItem;
